func (ccs *CalveCountSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range ccs.fields {
		if !calvecount.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := ccs.sqlQuery().Query()
	if err := ccs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (ccs *CalveCountSelect) sqlQuery() sql.Querier {
	selector := ccs.sql
	selector.Select(selector.Columns(ccs.fields...)...)
	return selector
}
