func (cts *CalveTypeSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range cts.fields {
		if !calvetype.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := cts.sqlQuery().Query()
	if err := cts.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (cts *CalveTypeSelect) sqlQuery() sql.Querier {
	selector := cts.sql
	selector.Select(selector.Columns(cts.fields...)...)
	return selector
}
