// Float64s returns list of float64s from selector. It is only allowed when selecting one field.
func (cbs *CattleBreedSelect) Float64s(ctx context.Context) ([]float64, error) {
	if len(cbs.fields) > 1 {
		return nil, errors.New("ent: CattleBreedSelect.Float64s is not achievable when selecting more than 1 field")
	}
	var v []float64
	if err := cbs.Scan(ctx, &v); err != nil {
		return nil, err
	}
	return v, nil
}

// Float64sX is like Float64s, but panics if an error occurs.
func (cbs *CattleBreedSelect) Float64sX(ctx context.Context) []float64 {
	v, err := cbs.Float64s(ctx)
	if err != nil {
		panic(err)
	}
	return v
}

// Float64 returns a single float64 from selector. It is only allowed when selecting one field.
func (cbs *CattleBreedSelect) Float64(ctx context.Context) (_ float64, err error) {
	var v []float64
	if v, err = cbs.Float64s(ctx); err != nil {
		return
	}
	switch len(v) {
	case 1:
		return v[0], nil
	case 0:
		err = &NotFoundError{cattlebreed.Label}
	default:
		err = fmt.Errorf("ent: CattleBreedSelect.Float64s returned %d results when one was expected", len(v))
	}
	return
}

