func (cbs *CattleBreedSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range cbs.fields {
		if !cattlebreed.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := cbs.sqlQuery().Query()
	if err := cbs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (cbs *CattleBreedSelect) sqlQuery() sql.Querier {
	selector := cbs.sql
	selector.Select(selector.Columns(cbs.fields...)...)
	return selector
}
