func (cds *CattleDieSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range cds.fields {
		if !cattledie.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := cds.sqlQuery().Query()
	if err := cds.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (cds *CattleDieSelect) sqlQuery() sql.Querier {
	selector := cds.sql
	selector.Select(selector.Columns(cds.fields...)...)
	return selector
}
