func (cgs *CattleGrowSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range cgs.fields {
		if !cattlegrow.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := cgs.sqlQuery().Query()
	if err := cgs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (cgs *CattleGrowSelect) sqlQuery() sql.Querier {
	selector := cgs.sql
	selector.Select(selector.Columns(cgs.fields...)...)
	return selector
}
