func (cms *CattleMoveSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range cms.fields {
		if !cattlemove.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := cms.sqlQuery().Query()
	if err := cms.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

