func (dis *DiseaseInfoSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range dis.fields {
		if !diseaseinfo.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := dis.sqlQuery().Query()
	if err := dis.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (dis *DiseaseInfoSelect) sqlQuery() sql.Querier {
	selector := dis.sql
	selector.Select(selector.Columns(dis.fields...)...)
	return selector
}
