func (ets *EstrusTypeSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range ets.fields {
		if !estrustype.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := ets.sqlQuery().Query()
	if err := ets.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (ets *EstrusTypeSelect) sqlQuery() sql.Querier {
	selector := ets.sql
	selector.Select(selector.Columns(ets.fields...)...)
	return selector
}
