func (fis *FeedInfoSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range fis.fields {
		if !feedinfo.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := fis.sqlQuery().Query()
	if err := fis.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (fis *FeedInfoSelect) sqlQuery() sql.Querier {
	selector := fis.sql
	selector.Select(selector.Columns(fis.fields...)...)
	return selector
}
