func (frs *FeedRecordSelect) Float64(ctx context.Context) (_ float64, err error) {
	var v []float64
	if v, err = frs.Float64s(ctx); err != nil {
		return
	}
	switch len(v) {
	case 1:
		return v[0], nil
	case 0:
		err = &NotFoundError{feedrecord.Label}
	default:
		err = fmt.Errorf("ent: FeedRecordSelect.Float64s returned %d results when one was expected", len(v))
	}
	return
}

// Float64X is like Float64, but panics if an error occurs.
