func (frs *FeedRecordSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range frs.fields {
		if !feedrecord.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := frs.sqlQuery().Query()
	if err := frs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (frs *FeedRecordSelect) sqlQuery() sql.Querier {
	selector := frs.sql
	selector.Select(selector.Columns(frs.fields...)...)
	return selector
}
