func (hss *HairStateSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range hss.fields {
		if !hairstate.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := hss.sqlQuery().Query()
	if err := hss.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (hss *HairStateSelect) sqlQuery() sql.Querier {
	selector := hss.sql
	selector.Select(selector.Columns(hss.fields...)...)
	return selector
}
