func (hcs *HealthCareSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range hcs.fields {
		if !healthcare.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := hcs.sqlQuery().Query()
	if err := hcs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (hcs *HealthCareSelect) sqlQuery() sql.Querier {
	selector := hcs.sql
	selector.Select(selector.Columns(hcs.fields...)...)
	return selector
}
