func (rps *RationProcessSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range rps.fields {
		if !rationprocess.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := rps.sqlQuery().Query()
	if err := rps.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (rps *RationProcessSelect) sqlQuery() sql.Querier {
	selector := rps.sql
	selector.Select(selector.Columns(rps.fields...)...)
	return selector
}
