func (sss *ShedSettingSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range sss.fields {
		if !shedsetting.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := sss.sqlQuery().Query()
	if err := sss.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (sss *ShedSettingSelect) sqlQuery() sql.Querier {
	selector := sss.sql
	selector.Select(selector.Columns(sss.fields...)...)
	return selector
}
