func (tss *TreatmentStateSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range tss.fields {
		if !treatmentstate.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := tss.sqlQuery().Query()
	if err := tss.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (tss *TreatmentStateSelect) sqlQuery() sql.Querier {
	selector := tss.sql
	selector.Select(selector.Columns(tss.fields...)...)
	return selector
}
