func (wds *WindDirectionSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range wds.fields {
		if !winddirection.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := wds.sqlQuery().Query()
	if err := wds.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (wds *WindDirectionSelect) sqlQuery() sql.Querier {
	selector := wds.sql
	selector.Select(selector.Columns(wds.fields...)...)
	return selector
}
